import React, { useState } from "react";

export const AddTodo = ({ addTodo }) => {
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");

  const submit = (e) => {
    e.preventDefault();
    if (!title || !desc) {
      alert("Title or Description cannot be blank");
    } else {
      addTodo(title, desc);
      setTitle("");
      setDesc("");
    }
  };

  return (
    <div className="container my-5 p-4 shadow-lg rounded" style={{ maxWidth: "600px", backgroundColor: "#f8f9fa" }}>
      <h3 className="text-center mb-4 text-primary">üìù Add a New Todo</h3>
      <form onSubmit={submit}>
        <div className="mb-3">
          <label htmlFor="title" className="form-label fw-bold">
            üìå Todo Title
          </label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="form-control border-primary"
            id="title"
            placeholder="Enter title here"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="desc" className="form-label fw-bold">
            üßæ Description
          </label>
          <textarea
            rows="3"
            onChange={(e) => setDesc(e.target.value)}
            value={desc}
            className="form-control border-primary"
            id="desc"
            placeholder="Write something about this task..."
          ></textarea>
        </div>
        <div className="text-center">
          <button type="submit" className="btn btn-primary px-4">
            ‚ûï Add Todo
          </button>
        </div>
      </form>
    </div>
  );
};
