import React from "react";
import { FaTasks, FaRocket, FaHeart } from "react-icons/fa";

const About = () => {
  return (
    <div className="min-h-screen bg-gradient-to-tr from-purple-100 via-blue-50 to-pink-100 px-6 py-16">
      {/* Hero Section */}
      <div className="text-center mb-12">
        <h1 className="text-5xl font-extrabold text-purple-700 drop-shadow-lg">
          About Our Todo App ðŸ’¼
        </h1>
        <p className="text-lg text-gray-700 mt-4 max-w-2xl mx-auto leading-relaxed">
          We believe productivity should be simple, beautiful, and delightful. Thatâ€™s why we built
          this Todo App â€” your smart partner in daily task management.
        </p>
      </div>

      {/* Feature Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-10 max-w-6xl mx-auto">
        {/* Card 1 */}
        <div className="bg-white rounded-xl p-8 shadow-xl hover:shadow-2xl transition duration-300 border-t-4 border-indigo-400">
          <FaTasks className="text-5xl text-indigo-500 mb-4 mx-auto" />
          <h2 className="text-2xl font-semibold text-gray-800 mb-2 text-center">Task Management</h2>
          <p className="text-gray-600 text-center">
            Plan, create, and track your todos easily with our intuitive interface.
          </p>
        </div>

        {/* Card 2 */}
        <div className="bg-white rounded-xl p-8 shadow-xl hover:shadow-2xl transition duration-300 border-t-4 border-green-400">
          <FaRocket className="text-5xl text-green-500 mb-4 mx-auto" />
          <h2 className="text-2xl font-semibold text-gray-800 mb-2 text-center">Boost Focus</h2>
          <p className="text-gray-600 text-center">
            Stay productive with priority sorting, reminders, and clean visuals.
          </p>
        </div>

        {/* Card 3 */}
        <div className="bg-white rounded-xl p-8 shadow-xl hover:shadow-2xl transition duration-300 border-t-4 border-pink-400">
          <FaHeart className="text-5xl text-pink-500 mb-2 text-center" />
          <h2 className="text-2xl font-semibold text-gray-800 mb-2 text-center">Loved by Users</h2>
          <p className="text-gray-600 text-center">
            Designed with care to offer you a delightful and relaxing task experience.
          </p>
        </div>
      </div>

      
    </div>
  );
};

export default About;
