import logo from './logo.svg';
import './App.css';
import Header from './MyComponents/Header';
import { Footer } from './MyComponents/Footer';
import React, { useState } from 'react';
import { Todos } from './MyComponents/Todos';
import { AddTodo } from './MyComponents/AddTodo';
// import { Todo } from "./MyComponents/Todo"; // Only import if needed separately

function App() {
  const onDelete = (todo) => {
    console.log("I am on delete of todo", todo);
    setTodos(todos.filter((e) => e !== todo));
  };

  const addTodo = (title, desc) => {
    console.log("I am adding this todo", title, desc);
    let sno = todos.length > 0 ? todos[todos.length - 1].sno + 1 : 1;
    const myTodo = {
      sno: sno,
      title: title,
      desc: desc,
    };
    setTodos([...todos, myTodo]);
    // console.log(myTodo);
    console.log(myTodo);
  };

  const [todos, setTodos] = useState([
    {
      sno: 1,
      title: "Go to the market",
      desc: "You need to go to the market to get vegies",
    },
    {
      sno: 2,
      title: "Go to the mall",
      desc: "You need to go to the mall to get that dress",
    },
    {
      sno: 3,
      title: "Go to the park",
      desc: "You need to go to the park for walk ",
    },
  ]);

  return (
    <>
      <Header title="My Todo List" searchBar="false" />
      <AddTodo addTodo={addTodo} />
      <Todos todos={todos} onDelete={onDelete} />
      <Footer />
    </>
  );
}

export default App;
